integrationExamples.claudeStreaming:
const stream = await anthropic.messages.stream({
  model: 'claude-3-5-sonnet',
  messages: [{ role: 'user', content: prompt }],
  max_tokens: 1024
})

for await (const chunk of stream) {
  if (chunk.type === 'content_block_delta') {
    yield chunk.delta.text
  }
}

integrationExamples.promptBuilder:
function buildPrompt(template: string, variables: Record<string, string>): string {
  return template.replace(/{{(\w+)}}/g, (_, key) => variables[key] || '')
}

integrationExamples.caching:
const cacheKey = `generation:${hash(input + tone)}`
const cached = await redis.get(cacheKey)
if (cached) return cached

const result = await generateContent(input, tone)
await redis.setex(cacheKey, 3600, result)
return result
