integrationExamples.variations:
async function generateVariations(prompt: string, count: number) {
  const variations = []

  for (let i = 0; i < count; i++) {
    const result = await claude.generate(prompt, { temperature: 0.9 })
    variations.push(result)
  }

  return variations
}

integrationExamples.refinement:
async function refineGeneration(original: string, feedback: string) {
  const prompt = `Original:
${original}

User feedback: ${feedback}

Generate an improved version addressing the feedback.`

  return await claude.generate(prompt)
}
